import React, { useState, useEffect, useRef } from "react";
import { Send, Bot, User, Sparkles } from "lucide-react";
import { connectWebSocket } from "../api/noticeApi";

const NoticeChat = () => {
  const [messages, setMessages] = useState([
    {
      type: "bot",
      text: "안녕하세요! A_hub 공지 챗봇입니다. 궁금한 공지사항을 물어보세요! ✨",
    },
  ]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const socketRef = useRef(null);

  // WebSocket 연결
  useEffect(() => {
    if (!socketRef.current) {
      const ws = connectWebSocket((data) => {
        setIsTyping(false);
        setMessages((prev) => [...prev, { type: "bot", text: data.message }]);
      });

      ws.onopen = () => {
        console.log("WebSocket connected");
      };

      ws.onclose = () => {
        console.log("WebSocket disconnected");
      };

      ws.onerror = (err) => {
        console.error("WebSocket error", err);
      };

      socketRef.current = ws;
    }

    return () => {
      socketRef.current?.close();
      socketRef.current = null;
    };
  }, []);

  // 메시지 전송
  const sendMessage = () => {
    if (!input.trim()) return;

    const ws = socketRef.current;

    if (!ws) {
      console.log("WebSocket not initialized.");
      return;
    }

    const send = () => {
      ws.send(JSON.stringify({ message: input }));
      setMessages((prev) => [...prev, { type: "user", text: input }]);
      setInput("");
      setIsTyping(true);
    };

    if (ws.readyState === WebSocket.OPEN) {
      send();
    } else if (ws.readyState === WebSocket.CONNECTING) {
      console.log("WebSocket is connecting, will send after open...");
      ws.onopen = () => send();
    } else {
      console.log("WebSocket is closed. Cannot send message.");
    }
  };

  return (
    <div className="bg-white rounded-2xl shadow-xl border border-gray-100 overflow-hidden">
      {/* 챗봇 헤더 */}
      <div className="bg-gradient-to-r from-blue-600 via-purple-600 to-blue-700 px-6 py-4">
        <div className="flex items-center space-x-3">
          <div className="relative">
            <div className="w-10 h-10 bg-white/20 rounded-full flex items-center justify-center backdrop-blur-sm">
              <Bot className="w-6 h-6 text-white" />
            </div>
            <div className="absolute -top-1 -right-1 w-4 h-4 bg-green-400 rounded-full border-2 border-white animate-pulse"></div>
          </div>
          <div>
            <h3 className="text-white font-semibold text-lg">
              A_hub 공지 어시스턴트
            </h3>
            <p className="text-blue-100 text-sm">
              항상 도움을 드릴 준비가 되어있어요
            </p>
          </div>
          <div className="ml-auto">
            <Sparkles className="w-5 h-5 text-yellow-300 animate-pulse" />
          </div>
        </div>
      </div>

      {/* 메시지 영역 */}
      <div className="h-96 overflow-y-auto p-4 space-y-4 bg-gray-50">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`flex ${
              msg.type === "user" ? "justify-end" : "justify-start"
            } animate-fade-in`}
          >
            <div className={`flex items-start space-x-3 max-w-xs lg:max-w-md`}>
              {msg.type === "bot" && (
                <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full flex items-center justify-center flex-shrink-0">
                  <Bot className="w-5 h-5 text-white" />
                </div>
              )}

              <div
                className={`px-4 py-3 rounded-2xl shadow-sm ${
                  msg.type === "user"
                    ? "bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-br-md"
                    : "bg-white text-gray-800 rounded-bl-md border border-gray-100"
                }`}
              >
                <p className="text-sm leading-relaxed">{msg.text}</p>
              </div>

              {msg.type === "user" && (
                <div className="w-8 h-8 bg-gradient-to-r from-gray-400 to-gray-500 rounded-full flex items-center justify-center flex-shrink-0">
                  <User className="w-5 h-5 text-white" />
                </div>
              )}
            </div>
          </div>
        ))}

        {isTyping && (
          <div className="flex justify-start animate-fade-in">
            <div className="flex items-start space-x-3">
              <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full flex items-center justify-center">
                <Bot className="w-5 h-5 text-white" />
              </div>
              <div className="bg-white px-4 py-3 rounded-2xl rounded-bl-md border border-gray-100 shadow-sm">
                <div className="flex space-x-1">
                  <div className="w-2 h-2 bg-blue-400 rounded-full animate-bounce"></div>
                  <div
                    className="w-2 h-2 bg-blue-400 rounded-full animate-bounce"
                    style={{ animationDelay: "0.1s" }}
                  ></div>
                  <div
                    className="w-2 h-2 bg-blue-400 rounded-full animate-bounce"
                    style={{ animationDelay: "0.2s" }}
                  ></div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* 입력 영역 */}
      <div className="p-4 border-t border-gray-100 bg-white">
        <div className="flex space-x-3 items-end">
          <div className="flex-1 relative">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && sendMessage()}
              placeholder="공지사항에 대해 궁금한 것을 물어보세요..."
              className="w-full px-4 py-3 border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none resize-none bg-gray-50 text-gray-800 placeholder-gray-500"
            />
          </div>
          <button
            onClick={sendMessage}
            disabled={!input.trim()}
            className={`px-4 py-3 rounded-xl transition-all duration-200 ${
              input.trim()
                ? "bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white shadow-lg hover:shadow-xl transform hover:scale-105"
                : "bg-gray-200 text-gray-400 cursor-not-allowed"
            }`}
          >
            <Send className="w-5 h-5" />
          </button>
        </div>

        {/* 추천 질문 */}
        <div className="mt-3 flex flex-wrap gap-2">
          {["최신 공지 알려줘", "중요 공지가 뭐야?", "내 부서 공지는?"].map(
            (suggestion, idx) => (
              <button
                key={idx}
                onClick={() => setInput(suggestion)}
                className="px-3 py-1 text-xs bg-blue-50 text-blue-600 rounded-full hover:bg-blue-100 transition-colors border border-blue-200"
              >
                {suggestion}
              </button>
            )
          )}
        </div>
      </div>
    </div>
  );
};

export default NoticeChat;
