import React, { useEffect, useState } from "react";
import { Bell, Search, MessageCircle } from "lucide-react";
import axios from "axios";

export default function NoticeList() {
  const [notices, setNotices] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/notices")
      .then((res) => {
        // 최신순으로 정렬 (postDate 기준, 내림차순)
        const sortedNotices = res.data.sort(
          (a, b) => new Date(b.postDate) - new Date(a.postDate)
        );
        setNotices(sortedNotices);
      })
      .catch((error) => {
        console.error("공지사항 로딩 실패:", error);
        // 에러 시 샘플 데이터 사용
        setNotices([
          {
            id: 1,
            seq: "2024-001",
            department: "개발팀",
            content: "시스템 업데이트 안내",
            postDate: "2024-03-15",
          },
          {
            id: 2,
            seq: "2024-002",
            department: "인사팀",
            content: "신입사원 환영회 개최",
            postDate: "2024-03-14",
          },
          {
            id: 3,
            seq: "2024-003",
            department: "총무팀",
            content: "사무용품 신청 안내",
            postDate: "2024-03-13",
          },
        ]);
      });
  }, []);

  const filteredNotices = notices.filter(
    (notice) =>
      notice.content.toLowerCase().includes(searchTerm.toLowerCase()) ||
      notice.department.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="bg-white rounded-2xl shadow-xl border border-gray-100 overflow-hidden">
      {/* 헤더 */}
      <div className="bg-gradient-to-r from-purple-600 via-blue-600 to-purple-700 px-6 py-5">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-white/20 rounded-full flex items-center justify-center backdrop-blur-sm">
              <Bell className="w-6 h-6 text-white" />
            </div>
            <div>
              <h2 className="text-white font-semibold text-xl">
                전체 공지사항
              </h2>
              <p className="text-blue-100 text-sm">최신 공지를 확인하세요</p>
            </div>
          </div>
          <div className="text-white/80 text-sm font-medium bg-white/20 px-3 py-1 rounded-full backdrop-blur-sm">
            {filteredNotices.length}개
          </div>
        </div>

        {/* 검색 바 */}
        <div className="mt-4 relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
          <input
            type="text"
            placeholder="공지사항 검색..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-3 bg-white/90 backdrop-blur-sm rounded-xl border-0 outline-none focus:ring-2 focus:ring-white/50 text-gray-800 placeholder-gray-500"
          />
        </div>
      </div>

      {/* 공지 리스트 */}
      <div className="max-h-96 overflow-y-auto">
        {filteredNotices.length === 0 ? (
          <div className="p-8 text-center text-gray-500">
            <Bell className="w-12 h-12 text-gray-300 mx-auto mb-3" />
            <p>검색 결과가 없습니다.</p>
          </div>
        ) : (
          <div className="p-6 space-y-4">
            {filteredNotices.map((notice) => (
              <div
                key={notice.id}
                className="bg-gray-50 rounded-xl p-5 hover:bg-gray-100 transition-all duration-200 border border-gray-200 hover:shadow-md cursor-pointer group"
              >
                <div className="flex items-start justify-between mb-3">
                  <div className="flex items-center space-x-3">
                    <span className="text-sm font-semibold text-blue-600 bg-blue-50 px-2 py-1 rounded-md">
                      {notice.seq}
                    </span>
                  </div>
                  <span className="text-xs text-gray-500 bg-gray-200 px-2 py-1 rounded-md">
                    {notice.postDate}
                  </span>
                </div>

                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <h3 className="font-semibold text-gray-800 mb-1 group-hover:text-blue-600 transition-colors">
                      {notice.content}
                    </h3>
                    <p className="text-sm text-gray-600 flex items-center">
                      <span className="w-2 h-2 bg-purple-500 rounded-full mr-2"></span>
                      {notice.department}
                    </p>
                  </div>
                  <div className="ml-4 opacity-0 group-hover:opacity-100 transition-opacity">
                    <MessageCircle className="w-5 h-5 text-gray-400" />
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
