import React from "react";
import ScheduleHeader from "./ScheduleHeader";
import TaskInput from "./TaskInput";
import TaskItem from "./TaskItem";
import CalendarGrid from "../calendar/CalendarGrid";
import useScheduleCalendar from "../../hooks/useScheduleCalendar";

const ScheduleView = ({ user, onLoginClick }) => {
  const {
    tasks,
    newTask,
    setNewTask,
    addTask,
    toggleComplete,
    deleteTask,
    events,
    currentDate,
    getDaysInMonth,
    getEventsForDate,
    prevMonth,
    nextMonth,
    loading,
    error,
  } = useScheduleCalendar(user);

  const completedTasks = tasks.filter((t) => t.completed).length;

  if (!user) {
    return (
      <div className="h-full flex flex-col items-center justify-center text-gray-500 space-y-4">
        <p>로그인 후 사용 가능합니다.</p>
        <button
          onClick={onLoginClick}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
        >
          로그인
        </button>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      <ScheduleHeader
        totalTasks={tasks.length}
        completedTasks={completedTasks}
      />

      <TaskInput value={newTask} onChange={setNewTask} onAdd={addTask} />

      {loading && <p className="text-center text-gray-400 mt-2">로딩 중...</p>}
      {error && <p className="text-center text-red-500 mt-2">{error}</p>}

      {/* 할 일 리스트 */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3 bg-gray-50 mt-4">
        {tasks.length === 0 ? (
          <div className="text-center py-12 text-gray-500">
            <p className="text-lg font-medium mb-1">할 일이 없습니다</p>
            <p className="text-sm">새로운 할 일을 추가해보세요</p>
          </div>
        ) : (
          <>
            {tasks
              .filter((t) => !t.completed)
              .map((task) => (
                <TaskItem
                  key={task.id}
                  task={task}
                  onToggle={() => toggleComplete(task.id)}
                  onDelete={() => deleteTask(task.id)}
                />
              ))}
            {tasks
              .filter((t) => t.completed)
              .map((task) => (
                <TaskItem
                  key={task.id}
                  task={task}
                  onToggle={() => toggleComplete(task.id)}
                  onDelete={() => deleteTask(task.id)}
                />
              ))}
          </>
        )}
      </div>

      {/* 달력 연동 */}
      <div className="mt-6">
        <CalendarGrid
          currentDate={currentDate}
          getDaysInMonth={getDaysInMonth}
          getEventsForDate={getEventsForDate}
        />
      </div>
    </div>
  );
};

export default ScheduleView;
