import React from "react";
import { Clock } from "lucide-react";
import ScheduleHeader from "./ScheduleHeader";
import TaskItem from "./TaskItem";
import TaskInput from "./TaskInput";
import useScheduleCalendar from "../../hooks/useScheduleCalendar";

const ScheduleView = ({ user, onLoginClick }) => {
  const {
    tasks,
    newTask,
    setNewTask,
    addTask,
    toggleComplete,
    deleteTask,
    events,
    currentDate,
    getDaysInMonth,
    prevMonth,
    nextMonth,
  } = useScheduleCalendar(user);

  const completedTasks = tasks.filter((t) => t.completed).length;
  const { firstDay, daysInMonth } = getDaysInMonth();

  if (!user) {
    return (
      <div className="h-full flex flex-col items-center justify-center text-gray-500 space-y-4">
        <p>로그인 후 사용 가능합니다.</p>
        <button
          onClick={onLoginClick}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
        >
          로그인
        </button>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* 헤더 */}
      <ScheduleHeader
        totalTasks={tasks.length}
        completedTasks={completedTasks}
      />

      {/* 할 일 추가 */}
      <TaskInput value={newTask} onChange={setNewTask} onAdd={addTask} />

      {/* 캘린더 */}
      <div className="grid grid-cols-7 gap-2 mt-6">
        {Array.from({ length: firstDay }).map((_, idx) => (
          <div key={`empty-${idx}`} />
        ))}
        {Array.from({ length: daysInMonth }).map((_, idx) => {
          const day = idx + 1;
          const dayEvents = events.filter(
            (e) =>
              e.date.getDate() === day &&
              e.date.getMonth() === currentDate.getMonth() &&
              e.date.getFullYear() === currentDate.getFullYear()
          );

          return (
            <div
              key={day}
              className="border p-2 rounded min-h-[60px] flex flex-col"
            >
              <span className="font-bold">{day}</span>
              {dayEvents.map((event) => (
                <span
                  key={event.id}
                  className={`text-sm ${
                    event.type === "schedule"
                      ? "text-blue-500"
                      : "text-purple-500"
                  }`}
                >
                  {event.title}
                </span>
              ))}
            </div>
          );
        })}
      </div>

      {/* 이전/다음 달 */}
      <div className="flex justify-between mt-4">
        <button onClick={prevMonth} className="px-4 py-2 bg-gray-300 rounded">
          이전
        </button>
        <button onClick={nextMonth} className="px-4 py-2 bg-gray-300 rounded">
          다음
        </button>
      </div>

      {/* 할 일 목록 */}
      <div className="flex-1 overflow-y-auto p-6 space-y-3 bg-gray-50 mt-6">
        {tasks.length === 0 ? (
          <div className="text-center py-12 text-gray-500">
            <Clock className="w-12 h-12 text-gray-300 mx-auto mb-3" />
            <p className="text-lg font-medium mb-1">할 일이 없습니다</p>
            <p className="text-sm">아래에서 새로운 할 일을 추가해보세요</p>
          </div>
        ) : (
          <>
            {tasks
              .filter((t) => !t.completed)
              .map((task) => (
                <TaskItem
                  key={task.id}
                  task={task}
                  onToggle={toggleComplete}
                  onDelete={deleteTask}
                />
              ))}
            {tasks
              .filter((t) => t.completed)
              .map((task) => (
                <TaskItem
                  key={task.id}
                  task={task}
                  onToggle={toggleComplete}
                  onDelete={deleteTask}
                />
              ))}
          </>
        )}
      </div>
    </div>
  );
};

export default ScheduleView;
