import React, { useState } from "react";
import { Plus, ChevronDown } from "lucide-react";

const TaskInput = ({ value, onChange, onAdd }) => {
  const [priority, setPriority] = useState("medium");
  const [dueDate, setDueDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const [showOptions, setShowOptions] = useState(false);

  // Enter 키 처리
  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleAdd();
    }
  };

  // 추가 버튼 클릭 시
  const handleAdd = () => {
    if (!value.trim()) return;
    onAdd({ title: value, priority, dueDate });
    // 초기화
    setPriority("medium");
    setDueDate(new Date().toISOString().split("T")[0]);
  };

  return (
    <div className="bg-white rounded-2xl shadow-sm p-6">
      {/* 메인 입력 영역 */}
      <div className="flex items-center gap-3">
        {/* 텍스트 입력 */}
        <input
          type="text"
          value={value}
          onChange={(e) => onChange(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="새로운 할 일 추가..."
          className="flex-1 px-4 py-3 bg-gray-50 border-none rounded-xl text-gray-800 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-gray-200"
        />

        {/* 추가 버튼 */}
        <button
          onClick={handleAdd}
          disabled={!value.trim()}
          className={`w-12 h-12 rounded-full flex items-center justify-center transition-all ${
            value.trim()
              ? "bg-gray-200 hover:bg-gray-300 text-gray-700"
              : "bg-gray-100 text-gray-400 cursor-not-allowed"
          }`}
          title="할 일 추가"
        >
          <Plus className="w-5 h-5" />
        </button>
      </div>

      {/* 상세 옵션 토글 */}
      <button
        onClick={() => setShowOptions(!showOptions)}
        className="mt-3 flex items-center gap-1 text-sm text-gray-500 hover:text-gray-700 transition-colors"
      >
        <ChevronDown
          className={`w-4 h-4 transition-transform ${
            showOptions ? "rotate-180" : ""
          }`}
        />
        <span>상세 옵션</span>
      </button>

      {/* 상세 옵션 영역 */}
      {showOptions && (
        <div className="mt-4 space-y-4 animate-fade-in">
          {/* 중요도 선택 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              중요도
            </label>
            <div className="flex gap-2">
              <button
                onClick={() => setPriority("high")}
                className={`flex-1 py-2.5 px-4 rounded-lg text-sm font-medium transition-all ${
                  priority === "high"
                    ? "bg-red-50 text-red-600 border-2 border-red-200"
                    : "bg-gray-50 text-gray-600 border-2 border-transparent hover:bg-gray-100"
                }`}
              >
                높음
              </button>
              <button
                onClick={() => setPriority("medium")}
                className={`flex-1 py-2.5 px-4 rounded-lg text-sm font-medium transition-all ${
                  priority === "medium"
                    ? "bg-yellow-50 text-yellow-600 border-2 border-yellow-200"
                    : "bg-gray-50 text-gray-600 border-2 border-transparent hover:bg-gray-100"
                }`}
              >
                보통
              </button>
              <button
                onClick={() => setPriority("low")}
                className={`flex-1 py-2.5 px-4 rounded-lg text-sm font-medium transition-all ${
                  priority === "low"
                    ? "bg-blue-50 text-blue-600 border-2 border-blue-200"
                    : "bg-gray-50 text-gray-600 border-2 border-transparent hover:bg-gray-100"
                }`}
              >
                낮음
              </button>
            </div>
          </div>

          {/* 마감일 선택 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              마감일
            </label>
            <div className="flex gap-2">
              <button
                onClick={() =>
                  setDueDate(new Date().toISOString().split("T")[0])
                }
                className="px-4 py-2.5 text-sm font-medium bg-gray-50 text-gray-600 rounded-lg hover:bg-gray-100 transition-colors"
              >
                오늘
              </button>
              <button
                onClick={() => {
                  const tomorrow = new Date();
                  tomorrow.setDate(tomorrow.getDate() + 1);
                  setDueDate(tomorrow.toISOString().split("T")[0]);
                }}
                className="px-4 py-2.5 text-sm font-medium bg-gray-50 text-gray-600 rounded-lg hover:bg-gray-100 transition-colors"
              >
                내일
              </button>
              <input
                type="date"
                value={dueDate}
                onChange={(e) => setDueDate(e.target.value)}
                className="flex-1 px-4 py-2.5 text-sm bg-gray-50 border-none rounded-lg focus:outline-none focus:ring-2 focus:ring-gray-200"
              />
            </div>
          </div>
        </div>
      )}

      {/* 도움말 텍스트 */}
      <div className="mt-3 text-xs text-gray-400">
        Enter 키를 눌러 빠르게 추가 · {value.length}자
      </div>
    </div>
  );
};

// 데모를 위한 래퍼
const TaskInputDemo = () => {
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState("");

  const handleAdd = ({ title, priority, dueDate }) => {
    setTasks((prev) => [
      {
        id: Date.now(),
        title,
        priority,
        dueDate,
        completed: false,
      },
      ...prev,
    ]);
    setInputValue("");
  };

  const toggleComplete = (id) => {
    setTasks((prev) =>
      prev.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const deleteTask = (id) => {
    setTasks((prev) => prev.filter((task) => task.id !== id));
  };

  const priorityConfig = {
    high: { color: "text-red-600", bg: "bg-red-50" },
    medium: { color: "text-yellow-600", bg: "bg-yellow-50" },
    low: { color: "text-blue-600", bg: "bg-blue-50" },
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      {/* 헤더 */}
      <div className="bg-green-600 text-white p-6 shadow-md">
        <div className="max-w-4xl mx-auto">
          <p className="text-sm mb-2">안녕하세요, rlawldms0615 님!</p>
          <div className="flex items-center justify-between">
            <h1 className="text-xl font-bold">나의 일정 관리</h1>
            <div className="text-right">
              <div className="text-xs text-green-100">완료율</div>
              <div className="text-2xl font-bold">
                {tasks.length === 0
                  ? "0%"
                  : Math.round(
                      (tasks.filter((t) => t.completed).length / tasks.length) *
                        100
                    ) + "%"}
              </div>
              <div className="text-xs text-green-100">
                {tasks.filter((t) => t.completed).length} / {tasks.length} 완료
              </div>
            </div>
          </div>
          <p className="text-sm text-green-100 mt-2">
            할 일을 체크하고 관리하세요
          </p>
        </div>
      </div>

      {/* 메인 컨텐츠 */}
      <div className="flex-1 max-w-4xl mx-auto w-full p-6 flex flex-col">
        {/* 작업 목록 */}
        <div className="flex-1 space-y-3 mb-6">
          {tasks.map((task) => (
            <div
              key={task.id}
              className="bg-white rounded-xl shadow-sm p-4 hover:shadow-md transition-shadow"
            >
              <div className="flex items-start gap-3">
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => toggleComplete(task.id)}
                  className="mt-1 w-5 h-5 rounded border-gray-300 text-green-600 focus:ring-green-500"
                />
                <div className="flex-1">
                  <h3
                    className={`font-medium ${
                      task.completed
                        ? "text-gray-400 line-through"
                        : "text-gray-800"
                    }`}
                  >
                    {task.title}
                  </h3>
                  <div className="mt-2 flex items-center gap-3 text-sm">
                    <span
                      className={`px-2 py-1 rounded-md ${
                        priorityConfig[task.priority].bg
                      } ${priorityConfig[task.priority].color} font-medium`}
                    >
                      {task.priority === "high"
                        ? "높음"
                        : task.priority === "medium"
                        ? "보통"
                        : "낮음"}
                    </span>
                    <span className="text-gray-500">
                      {new Date(task.dueDate).toLocaleDateString("ko-KR")}
                    </span>
                  </div>
                </div>
                <button
                  onClick={() => deleteTask(task.id)}
                  className="text-gray-400 hover:text-red-500 transition-colors"
                >
                  ✕
                </button>
              </div>
            </div>
          ))}

          {tasks.length === 0 && (
            <div className="text-center py-16 text-gray-400">
              할 일을 추가해보세요!
            </div>
          )}
        </div>

        {/* 입력 영역 (하단 고정) */}
        <TaskInput
          value={inputValue}
          onChange={setInputValue}
          onAdd={handleAdd}
        />
      </div>

      {/* 푸터 */}
      <div className="text-center py-6 text-sm text-gray-400">
        © 2025 A hub. All rights reserved.
      </div>
    </div>
  );
};

export default TaskInputDemo;
