import React from "react";
import { Wifi, WifiOff } from "lucide-react";
import { CONNECTION_COLORS } from "../../utils/constants";

const ConnectionBadge = ({ status }) => {
  // 상태별 색상 가져오기
  const getColor = () => {
    return CONNECTION_COLORS[status] || CONNECTION_COLORS.disconnected;
  };

  // 상태별 아이콘 가져오기
  const getIcon = () => {
    if (status === "connected") {
      return <Wifi className="w-3 h-3 text-white" />;
    }
    if (status === "disconnected") {
      return <WifiOff className="w-3 h-3 text-white" />;
    }
    return null; // connecting 상태는 아이콘 없음 (애니메이션만)
  };

  return (
    <div
      className={`absolute -top-1 -right-1 w-4 h-4 ${getColor()} rounded-full border-2 border-white flex items-center justify-center`}
    >
      {getIcon()}
    </div>
  );
};

export default ConnectionBadge;
