// src/hooks/useScheduleCalendar.js
import { useState, useEffect } from "react";
import {
  getUserSchedules,
  createSchedule,
  toggleCompleteSchedule,
  deleteSchedule,
  getUserEvents,
  createEvent, // ✅ 추가
} from "../services/api";

const useScheduleCalendar = (user) => {
  const [tasks, setTasks] = useState([]);
  const [events, setEvents] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentDate, setCurrentDate] = useState(new Date());

  // ---------- 초기 로드 ----------
  const loadSchedules = async () => {
    if (!user) return;
    try {
      setLoading(true);
      const schedules = await getUserSchedules(user.id);
      setTasks(schedules);

      const scheduleEvents = schedules.map((schedule) => ({
        id: `schedule-${schedule.id}`,
        date: new Date(schedule.dueDate),
        title: schedule.title,
        type: "schedule",
        priority: schedule.priority,
        completed: schedule.completed,
      }));

      setEvents((prev) => {
        const nonScheduleEvents = prev.filter((e) => e.type !== "schedule");
        return [...nonScheduleEvents, ...scheduleEvents];
      });
    } catch (err) {
      console.error(err);
      setError("스케줄 불러오기 실패");
    } finally {
      setLoading(false);
    }
  };

  const loadEvents = async () => {
    if (!user) return;
    try {
      const calendarData = await getUserEvents(user.id);
      const calendarEvents = calendarData.map((event) => ({
        id: `event-${event.id}`,
        date: new Date(event.eventDate),
        title: event.title,
        type: event.eventType || "event",
        priority: event.priority || "medium",
        completed: event.completed || false,
      }));

      setEvents((prev) => {
        const scheduleEvents = prev.filter((e) => e.type === "schedule");
        return [...scheduleEvents, ...calendarEvents];
      });
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    loadSchedules();
    loadEvents();
  }, [user]);

  // ---------- 할 일 추가 & 이벤트 생성 ----------
  const addTask = async () => {
    if (!newTask.trim() || !user) return;

    try {
      const todayStr = new Date().toISOString().split("T")[0];

      // 1️⃣ 스케줄 생성
      const savedTask = await createSchedule({
        userId: user.id,
        title: newTask,
        priority: "medium",
        dueDate: todayStr,
        description: "",
      });

      setTasks((prev) => [savedTask, ...prev]);

      // 2️⃣ 이벤트 테이블에도 생성
      const savedEvent = await createEvent(user.id, {
        title: savedTask.title,
        description: savedTask.description,
        eventDate: savedTask.dueDate,
        eventTime: null,
        eventType: "schedule",
        location: null,
        completed: savedTask.completed,
        priority: savedTask.priority,
      });

      const newEvent = {
        id: `event-${savedEvent.id}`,
        date: new Date(savedEvent.eventDate),
        title: savedEvent.title,
        type: "schedule",
        completed: savedEvent.completed,
        priority: savedEvent.priority,
      };

      setEvents((prev) => [...prev, newEvent]);
      setNewTask("");
    } catch (err) {
      console.error(err);
      setError("작업 추가 실패");
    }
  };

  // ---------- 날짜별 이벤트 조회 ----------
  const getEventsForDate = (day) => {
    const targetDate = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth(),
      day
    );

    return events.filter(
      (event) =>
        event.date.getDate() === targetDate.getDate() &&
        event.date.getMonth() === targetDate.getMonth() &&
        event.date.getFullYear() === targetDate.getFullYear()
    );
  };

  const toggleComplete = async (id) => {
    try {
      const updatedTask = await toggleCompleteSchedule(id);

      setTasks((prev) =>
        prev.map((task) => (task.id === id ? updatedTask : task))
      );

      setEvents((prev) =>
        prev.map((event) =>
          event.id === `schedule-${id}`
            ? { ...event, completed: updatedTask.completed }
            : event
        )
      );
    } catch (err) {
      console.error(err);
    }
  };

  const deleteTaskById = async (id) => {
    try {
      await deleteSchedule(id);
      setTasks((prev) => prev.filter((task) => task.id !== id));
      setEvents((prev) =>
        prev.filter((event) => event.id !== `schedule-${id}`)
      );
    } catch (err) {
      console.error(err);
    }
  };

  const getDaysInMonth = () => {
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    const firstDay = new Date(year, month, 1).getDay();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    return { firstDay, daysInMonth };
  };

  const prevMonth = () =>
    setCurrentDate(
      new Date(currentDate.getFullYear(), currentDate.getMonth() - 1)
    );

  const nextMonth = () =>
    setCurrentDate(
      new Date(currentDate.getFullYear(), currentDate.getMonth() + 1)
    );

  return {
    tasks,
    newTask,
    setNewTask,
    addTask,
    toggleComplete,
    deleteTask: deleteTaskById,
    currentDate,
    events,
    getDaysInMonth,
    getEventsForDate,
    prevMonth,
    nextMonth,
    loading,
    error,
    refresh: () => {
      loadSchedules();
      loadEvents();
    },
  };
};

export default useScheduleCalendar;
