import React from "react";
import { ChevronLeft, ChevronRight } from "lucide-react";
import useScheduleCalendar from "../../hooks/useScheduleCalendar";

const CalendarView = ({ tasks }) => {
  const { currentDate, getDaysInMonth, prevMonth, nextMonth } =
    useScheduleCalendar();

  // 날짜별 이벤트 가져오기
  const getEventsForDate = (day) => {
    if (!day) return [];
    const dateStr = `${currentDate.getFullYear()}-${
      currentDate.getMonth() + 1
    }-${day}`;
    return tasks.filter((task) => task.date === dateStr);
  };
  const { firstDay, daysInMonth } = getDaysInMonth();
  const monthNames = [
    "1월",
    "2월",
    "3월",
    "4월",
    "5월",
    "6월",
    "7월",
    "8월",
    "9월",
    "10월",
    "11월",
    "12월",
  ];
  const dayNames = ["일", "월", "화", "수", "목", "금", "토"];

  // 달력 그리드 생성
  const calendarDays = [];

  // 빈 칸 추가 (첫 주)
  for (let i = 0; i < firstDay; i++) {
    calendarDays.push(null);
  }

  // 날짜 추가
  for (let day = 1; day <= daysInMonth; day++) {
    calendarDays.push(day);
  }

  // 이벤트 타입별 색상
  const getEventColor = (event) => {
    if (event.type === "schedule") {
      if (event.completed) return "bg-gray-400";
      switch (event.priority) {
        case "high":
          return "bg-red-500";
        case "medium":
          return "bg-yellow-500";
        case "low":
          return "bg-green-500";
        default:
          return "bg-blue-500";
      }
    }
    return "bg-purple-500";
  };

  return (
    <div className="h-full flex flex-col bg-white">
      {/* 헤더 */}
      <div className="border-b px-6 py-4">
        <div className="flex items-center justify-between">
          <h2 className="text-2xl font-bold text-gray-800">
            {currentDate.getFullYear()}년 {monthNames[currentDate.getMonth()]}
          </h2>
          <div className="flex space-x-2">
            <button
              onClick={prevMonth}
              className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            >
              <ChevronLeft className="w-5 h-5" />
            </button>
            <button
              onClick={nextMonth}
              className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            >
              <ChevronRight className="w-5 h-5" />
            </button>
          </div>
        </div>
      </div>

      {/* 요일 헤더 */}
      <div className="grid grid-cols-7 border-b">
        {dayNames.map((day, index) => (
          <div
            key={day}
            className={`py-3 text-center text-sm font-semibold ${
              index === 0
                ? "text-red-600"
                : index === 6
                ? "text-blue-600"
                : "text-gray-700"
            }`}
          >
            {day}
          </div>
        ))}
      </div>

      {/* 달력 그리드 */}
      <div className="flex-1 grid grid-cols-7 auto-rows-fr">
        {calendarDays.map((day, index) => {
          const dayEvents = day ? getEventsForDate(day) : [];
          const isToday =
            day &&
            day === new Date().getDate() &&
            currentDate.getMonth() === new Date().getMonth() &&
            currentDate.getFullYear() === new Date().getFullYear();

          return (
            <div
              key={index}
              className={`border-r border-b p-2 overflow-hidden ${
                !day ? "bg-gray-50" : ""
              }`}
            >
              {day && (
                <>
                  <div className="flex justify-between items-start mb-1">
                    <span
                      className={`text-sm font-medium ${
                        isToday
                          ? "bg-blue-600 text-white w-6 h-6 rounded-full flex items-center justify-center"
                          : index % 7 === 0
                          ? "text-red-600"
                          : index % 7 === 6
                          ? "text-blue-600"
                          : "text-gray-700"
                      }`}
                    >
                      {day}
                    </span>
                  </div>

                  {/* 이벤트 표시 */}
                  <div className="space-y-1">
                    {dayEvents.slice(0, 3).map((event, i) => (
                      <div
                        key={i}
                        className={`text-xs px-2 py-1 rounded text-white truncate ${getEventColor(
                          event
                        )}`}
                        title={event.title}
                      >
                        {event.title}
                      </div>
                    ))}
                    {dayEvents.length > 3 && (
                      <div className="text-xs text-gray-500 px-2">
                        +{dayEvents.length - 3} 더보기
                      </div>
                    )}
                  </div>
                </>
              )}
            </div>
          );
        })}
      </div>

      {/* 범례 */}
      <div className="border-t px-6 py-3 bg-gray-50">
        <div className="flex items-center space-x-4 text-xs">
          <div className="flex items-center space-x-1">
            <div className="w-3 h-3 bg-red-500 rounded"></div>
            <span>높은 우선순위</span>
          </div>
          <div className="flex items-center space-x-1">
            <div className="w-3 h-3 bg-yellow-500 rounded"></div>
            <span>보통 우선순위</span>
          </div>
          <div className="flex items-center space-x-1">
            <div className="w-3 h-3 bg-green-500 rounded"></div>
            <span>낮은 우선순위</span>
          </div>
          <div className="flex items-center space-x-1">
            <div className="w-3 h-3 bg-purple-500 rounded"></div>
            <span>일정</span>
          </div>
          <div className="flex items-center space-x-1">
            <div className="w-3 h-3 bg-gray-400 rounded"></div>
            <span>완료</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CalendarView;
