// NoticeChat.js
import React, { useState, useEffect, useRef, useCallback } from "react";
import {
  Send,
  Bot,
  User,
  Sparkles,
  AlertCircle,
  Wifi,
  WifiOff,
} from "lucide-react";

const WS_URL = "ws://localhost:8080/ws/notice"; // 네이티브 WebSocket 엔드포인트

const NoticeChat = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      type: "bot",
      text: "안녕하세요! A_hub 공지 챗봇입니다. 궁금한 공지사항을 물어보세요! ✨",
      timestamp: new Date(),
    },
  ]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState("disconnected");
  const [error, setError] = useState(null);

  const wsRef = useRef(null);
  const messagesEndRef = useRef(null);
  const reconnectTimeoutRef = useRef(null);
  const messageIdRef = useRef(2);
  const heartbeatIntervalRef = useRef(null);

  // 자동 스크롤
  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  // 메시지 추가 함수
  const addMessage = useCallback((type, text, error = false) => {
    const newMessage = {
      id: messageIdRef.current++,
      type,
      text,
      timestamp: new Date(),
      error,
    };
    setMessages((prev) => [...prev, newMessage]);
  }, []);

  // WebSocket 연결 함수
  const connectWebSocket = useCallback(() => {
    try {
      setError(null);
      setConnectionStatus("connecting");

      const ws = new WebSocket(WS_URL);

      ws.onopen = () => {
        console.log("WebSocket connected");
        setConnectionStatus("connected");
        setError(null);

        // 연결 확인 메시지 전송
        ws.send(
          JSON.stringify({
            type: "connect",
            timestamp: new Date().toISOString(),
          })
        );

        // 하트비트 설정
        heartbeatIntervalRef.current = setInterval(() => {
          if (ws.readyState === WebSocket.OPEN) {
            ws.send(JSON.stringify({ type: "ping" }));
          }
        }, 30000); // 30초마다 ping
      };

      ws.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);

          // 메시지 타입에 따라 처리
          switch (data.type) {
            case "message":
            case "chat":
              setIsTyping(false);
              addMessage("bot", data.message || data.text || data.content);
              break;

            case "error":
              setIsTyping(false);
              addMessage("bot", data.message || "오류가 발생했습니다.", true);
              break;

            case "typing":
              setIsTyping(true);
              // 5초 후 자동으로 타이핑 상태 해제
              setTimeout(() => setIsTyping(false), 5000);
              break;

            case "pong":
              // 하트비트 응답 - 로그만 출력
              console.log("Received pong");
              break;

            default:
              // 일반 텍스트 메시지로 처리
              if (data.message || data.text) {
                setIsTyping(false);
                addMessage("bot", data.message || data.text);
              }
              break;
          }
        } catch (err) {
          console.error("Message parsing error:", err);
          // 파싱 실패 시 원본 메시지 표시
          setIsTyping(false);
          addMessage("bot", event.data);
        }
      };

      ws.onerror = (error) => {
        console.error("WebSocket error:", error);
        setError("연결 중 오류가 발생했습니다.");
      };

      ws.onclose = (event) => {
        console.log("WebSocket closed:", event.code, event.reason);
        setConnectionStatus("disconnected");
        setIsTyping(false);

        // 하트비트 정리
        if (heartbeatIntervalRef.current) {
          clearInterval(heartbeatIntervalRef.current);
          heartbeatIntervalRef.current = null;
        }

        // 정상 종료가 아닌 경우 자동 재연결
        if (event.code !== 1000) {
          setError("연결이 끊어졌습니다. 재연결을 시도합니다...");

          if (reconnectTimeoutRef.current) {
            clearTimeout(reconnectTimeoutRef.current);
          }
          reconnectTimeoutRef.current = setTimeout(connectWebSocket, 3000);
        }
      };

      wsRef.current = ws;
    } catch (error) {
      console.error("Failed to connect:", error);
      setConnectionStatus("disconnected");
      setError("연결 설정 중 오류가 발생했습니다.");
    }
  }, [addMessage]);

  // 메시지 전송
  const sendMessage = useCallback(() => {
    if (!input.trim()) return;

    const ws = wsRef.current;
    if (ws && ws.readyState === WebSocket.OPEN) {
      try {
        // 사용자 메시지 추가
        addMessage("user", input);

        // 서버로 메시지 전송
        ws.send(
          JSON.stringify({
            type: "chat",
            message: input,
            timestamp: new Date().toISOString(),
          })
        );

        setInput("");
        setIsTyping(true);

        // 타이핑 상태 자동 해제 (10초 후)
        setTimeout(() => {
          setIsTyping(false);
        }, 10000);
      } catch (error) {
        console.error("Failed to send message:", error);
        setIsTyping(false);
        addMessage(
          "bot",
          "메시지 전송 중 오류가 발생했습니다. 다시 시도해주세요.",
          true
        );
      }
    } else {
      addMessage(
        "bot",
        "서버에 연결되지 않았습니다. 잠시만 기다려주세요.",
        true
      );
    }
  }, [input, addMessage]);

  // 연결 해제 함수
  const disconnect = useCallback(() => {
    if (heartbeatIntervalRef.current) {
      clearInterval(heartbeatIntervalRef.current);
      heartbeatIntervalRef.current = null;
    }

    if (wsRef.current) {
      wsRef.current.close(1000, "User disconnected");
      wsRef.current = null;
    }
  }, []);

  // 수동 재연결
  const handleReconnect = useCallback(() => {
    disconnect();
    setTimeout(connectWebSocket, 1000);
  }, [disconnect, connectWebSocket]);

  // 추천 질문 클릭 핸들러
  const handleSuggestionClick = useCallback((suggestion) => {
    setInput(suggestion);
  }, []);

  // 키보드 이벤트
  const handleKeyDown = useCallback(
    (e) => {
      if (e.key === "Enter" && !e.shiftKey) {
        e.preventDefault();
        sendMessage();
      }
    },
    [sendMessage]
  );

  // 연결 상태에 따른 스타일
  const getConnectionStatusColor = () => {
    switch (connectionStatus) {
      case "connected":
        return "bg-green-400";
      case "connecting":
        return "bg-yellow-400 animate-pulse";
      case "disconnected":
        return "bg-red-400";
      default:
        return "bg-gray-400";
    }
  };

  const getConnectionIcon = () => {
    return connectionStatus === "connected" ? (
      <Wifi className="w-4 h-4 text-white" />
    ) : (
      <WifiOff className="w-4 h-4 text-white" />
    );
  };

  // 컴포넌트 마운트/언마운트
  useEffect(() => {
    connectWebSocket();

    return () => {
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
      disconnect();
    };
  }, [connectWebSocket, disconnect]);

  // 메시지 추가 시 스크롤
  useEffect(() => {
    scrollToBottom();
  }, [messages, scrollToBottom]);

  const isConnected = connectionStatus === "connected";

  return (
    <div className="bg-white rounded-2xl shadow-xl border border-gray-100 overflow-hidden">
      {/* 헤더 */}
      <div className="bg-gradient-to-r from-blue-600 via-purple-600 to-blue-700 px-6 py-4">
        <div className="flex items-center space-x-3">
          <div className="relative">
            <div className="w-10 h-10 bg-white/20 rounded-full flex items-center justify-center backdrop-blur-sm">
              <Bot className="w-6 h-6 text-white" />
            </div>
            <div
              className={`absolute -top-1 -right-1 w-4 h-4 ${getConnectionStatusColor()} rounded-full border-2 border-white flex items-center justify-center`}
            >
              {connectionStatus === "connecting" ? null : getConnectionIcon()}
            </div>
          </div>
          <div className="flex-1">
            <h3 className="text-white font-semibold text-lg">
              A_hub 공지 어시스턴트
            </h3>
            <p className="text-blue-100 text-sm">
              {connectionStatus === "connected" &&
                "항상 도움을 드릴 준비가 되어있어요"}
              {connectionStatus === "connecting" && "서버에 연결하는 중..."}
              {connectionStatus === "disconnected" && "연결이 끊어졌습니다"}
            </p>
          </div>
          <div className="flex items-center space-x-2">
            {error && <AlertCircle className="w-5 h-5 text-red-300" />}
            <Sparkles className="w-5 h-5 text-yellow-300 animate-pulse" />
          </div>
        </div>
      </div>

      {/* 에러 메시지 */}
      {error && (
        <div className="bg-red-50 border-b border-red-200 px-4 py-2">
          <div className="flex items-center space-x-2 text-red-700 text-sm">
            <AlertCircle className="w-4 h-4" />
            <span>{error}</span>
            <button
              onClick={handleReconnect}
              className="ml-auto px-2 py-1 text-xs bg-red-100 hover:bg-red-200 rounded transition-colors"
            >
              다시 연결
            </button>
          </div>
        </div>
      )}

      {/* 연결 안내 */}
      {!isConnected && !error && (
        <div className="bg-blue-50 border-b border-blue-200 px-4 py-2">
          <div className="flex items-center space-x-2 text-blue-700 text-sm">
            <div className="w-4 h-4 border-2 border-blue-400 border-t-transparent rounded-full animate-spin"></div>
            <span>
              서버 연결을 대기중입니다. 백엔드 서버가 실행되고 있는지
              확인해주세요.
            </span>
          </div>
        </div>
      )}

      {/* 메시지 영역 */}
      <div className="h-96 overflow-y-auto p-4 space-y-4 bg-gray-50">
        {messages.map((msg) => (
          <div
            key={msg.id}
            className={`flex ${
              msg.type === "user" ? "justify-end" : "justify-start"
            } animate-fade-in`}
          >
            <div className="flex items-start space-x-3 max-w-xs lg:max-w-md">
              {msg.type === "bot" && (
                <div
                  className={`w-8 h-8 ${
                    msg.error
                      ? "bg-red-500"
                      : "bg-gradient-to-r from-blue-500 to-purple-500"
                  } rounded-full flex items-center justify-center flex-shrink-0`}
                >
                  {msg.error ? (
                    <AlertCircle className="w-5 h-5 text-white" />
                  ) : (
                    <Bot className="w-5 h-5 text-white" />
                  )}
                </div>
              )}

              <div
                className={`px-4 py-3 rounded-2xl shadow-sm ${
                  msg.type === "user"
                    ? "bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-br-md"
                    : msg.error
                    ? "bg-red-50 text-red-800 rounded-bl-md border border-red-200"
                    : "bg-white text-gray-800 rounded-bl-md border border-gray-100"
                }`}
              >
                <p className="text-sm leading-relaxed">{msg.text}</p>
                <p className="text-xs opacity-70 mt-1">
                  {msg.timestamp.toLocaleTimeString()}
                </p>
              </div>

              {msg.type === "user" && (
                <div className="w-8 h-8 bg-gradient-to-r from-gray-400 to-gray-500 rounded-full flex items-center justify-center flex-shrink-0">
                  <User className="w-5 h-5 text-white" />
                </div>
              )}
            </div>
          </div>
        ))}

        {isTyping && (
          <div className="flex justify-start animate-fade-in">
            <div className="flex items-start space-x-3">
              <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full flex items-center justify-center">
                <Bot className="w-5 h-5 text-white" />
              </div>
              <div className="bg-white px-4 py-3 rounded-2xl rounded-bl-md border border-gray-100 shadow-sm">
                <div className="flex space-x-1">
                  <div className="w-2 h-2 bg-blue-400 rounded-full animate-bounce"></div>
                  <div
                    className="w-2 h-2 bg-blue-400 rounded-full animate-bounce"
                    style={{ animationDelay: "0.1s" }}
                  ></div>
                  <div
                    className="w-2 h-2 bg-blue-400 rounded-full animate-bounce"
                    style={{ animationDelay: "0.2s" }}
                  ></div>
                </div>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* 입력 영역 */}
      <div className="p-4 border-t border-gray-100 bg-white">
        <div className="flex space-x-3 items-end">
          <div className="flex-1 relative">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder={
                isConnected
                  ? "공지사항에 대해 궁금한 것을 물어보세요..."
                  : "서버 연결을 기다리는 중..."
              }
              disabled={!isConnected}
              className="w-full px-4 py-3 border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none resize-none bg-gray-50 text-gray-800 placeholder-gray-500 disabled:opacity-50 disabled:cursor-not-allowed"
            />
          </div>
          <button
            onClick={sendMessage}
            disabled={!input.trim() || !isConnected}
            className={`px-4 py-3 rounded-xl transition-all duration-200 ${
              input.trim() && isConnected
                ? "bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white shadow-lg hover:shadow-xl transform hover:scale-105"
                : "bg-gray-200 text-gray-400 cursor-not-allowed"
            }`}
          >
            <Send className="w-5 h-5" />
          </button>
        </div>

        {/* 추천 질문 */}
        <div className="mt-3 flex flex-wrap gap-2">
          {["최신 공지 알려줘", "중요 공지가 뭐야?", "내 부서 공지는?"].map(
            (suggestion, idx) => (
              <button
                key={idx}
                onClick={() => handleSuggestionClick(suggestion)}
                disabled={!isConnected}
                className="px-3 py-1 text-xs bg-blue-50 text-blue-600 rounded-full hover:bg-blue-100 transition-colors border border-blue-200 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {suggestion}
              </button>
            )
          )}
        </div>

        {/* 연결 상태 표시 */}
        <div className="mt-2 text-xs text-gray-500 flex items-center justify-between">
          <span>
            상태:{" "}
            {connectionStatus === "connected"
              ? "연결됨"
              : connectionStatus === "connecting"
              ? "연결 중..."
              : "연결 끊김"}
          </span>
          <span>WebSocket 연결</span>
        </div>
      </div>
    </div>
  );
};

export default NoticeChat;
