import React from "react";
import { Plus } from "lucide-react";

const TaskInput = ({ newTask, handleInputChange, onAdd }) => {
  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      onAdd();
    }
  };

  return (
    <div className="p-4 border-t bg-white flex-shrink-0">
      <div className="flex flex-col gap-2">
        {/* Ï†úÎ™© ÏûÖÎ†• */}
        <input
          type="text"
          value={newTask.title}
          onChange={(e) => handleInputChange("title", e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="ÏÉàÎ°úÏö¥ Ìï† Ïùº Ï∂îÍ∞Ä..."
          className="w-full px-4 py-2 border border-gray-200 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent outline-none bg-gray-50 text-gray-800 placeholder-gray-500"
        />

        <div className="flex gap-2">
          {/* Ï§ëÏöîÎèÑ ÏÑ†ÌÉù */}
          <select
            value={newTask.priority}
            onChange={(e) => handleInputChange("priority", e.target.value)}
            className="flex-1 px-3 py-2 border border-gray-200 rounded-xl bg-gray-50 focus:ring-2 focus:ring-green-500"
          >
            <option value="high">ÎÜíÏùå üî•</option>
            <option value="medium">Ï§ëÍ∞Ñ ‚öñÔ∏è</option>
            <option value="low">ÎÇÆÏùå üå±</option>
          </select>

          {/* ÎÇ†Ïßú ÏÑ†ÌÉù */}
          <input
            type="date"
            value={newTask.dueDate}
            onChange={(e) => handleInputChange("dueDate", e.target.value)}
            className="flex-1 px-3 py-2 border border-gray-200 rounded-xl bg-gray-50 focus:ring-2 focus:ring-green-500"
          />

          {/* Ï∂îÍ∞Ä Î≤ÑÌäº */}
          <button
            onClick={onAdd}
            disabled={!newTask.title.trim()}
            className={`px-4 py-2 rounded-xl transition-all ${
              newTask.title.trim()
                ? "bg-green-600 hover:bg-green-700 text-white shadow-lg hover:shadow-xl transform hover:scale-105"
                : "bg-gray-200 text-gray-400 cursor-not-allowed"
            }`}
            title="Ìï† Ïùº Ï∂îÍ∞Ä"
          >
            <Plus className="w-5 h-5" />
          </button>
        </div>
      </div>

      {/* ÎèÑÏõÄÎßê */}
      <div className="mt-2 text-xs text-gray-500 flex items-center justify-between">
        <span>Enter ÌÇ§Î•º ÎàåÎü¨ Îπ†Î•¥Í≤å Ï∂îÍ∞Ä</span>
        <span>{newTask.title.length}Ïûê</span>
      </div>
    </div>
  );
};

export default TaskInput;
