import React, { useState } from "react";
import { Plus, Calendar, Flag, ChevronDown } from "lucide-react";

const TaskInput = ({ value, onChange, onAdd }) => {
  const [priority, setPriority] = useState("medium");
  const [dueDate, setDueDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const [showOptions, setShowOptions] = useState(false);

  // Enter 키 처리
  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleAdd();
    }
  };

  // 추가 버튼 클릭 시
  const handleAdd = () => {
    if (!value.trim()) return;
    onAdd({ title: value, priority, dueDate });
    // 초기화
    setPriority("medium");
    setDueDate(new Date().toISOString().split("T")[0]);
  };

  // 우선순위 스타일
  const priorityConfig = {
    high: {
      label: "높음",
      color: "text-red-600",
      bg: "bg-red-50",
      border: "border-red-300",
    },
    medium: {
      label: "보통",
      color: "text-yellow-600",
      bg: "bg-yellow-50",
      border: "border-yellow-300",
    },
    low: {
      label: "낮음",
      color: "text-blue-600",
      bg: "bg-blue-50",
      border: "border-blue-300",
    },
  };

  const currentPriority = priorityConfig[priority];

  return (
    <div className="p-4 border-t bg-white flex-shrink-0">
      {/* 메인 입력 영역 */}
      <div className="flex space-x-2">
        <div className="flex-1">
          {/* 텍스트 입력 */}
          <input
            type="text"
            value={value}
            onChange={(e) => onChange(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="새로운 할 일 추가..."
            className="w-full px-4 py-2 border border-gray-200 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent outline-none bg-gray-50 text-gray-800 placeholder-gray-500"
          />

          {/* 옵션 토글 버튼 */}
          <button
            onClick={() => setShowOptions(!showOptions)}
            className="mt-2 flex items-center space-x-1 text-sm text-gray-600 hover:text-gray-800 transition-colors"
          >
            <ChevronDown
              className={`w-4 h-4 transition-transform ${
                showOptions ? "rotate-180" : ""
              }`}
            />
            <span>상세 옵션</span>
          </button>

          {/* 상세 옵션 (접히는 영역) */}
          {showOptions && (
            <div className="mt-3 p-3 bg-gray-50 rounded-lg space-y-3 border border-gray-200">
              {/* 중요도 선택 */}
              <div>
                <label className="block text-xs font-medium text-gray-700 mb-2">
                  <Flag className="w-3 h-3 inline mr-1" />
                  중요도
                </label>
                <div className="flex space-x-2">
                  {Object.entries(priorityConfig).map(([key, config]) => (
                    <button
                      key={key}
                      onClick={() => setPriority(key)}
                      className={`flex-1 py-2 px-3 rounded-lg text-sm font-medium transition-all ${
                        priority === key
                          ? `${config.bg} ${config.color} ${config.border} border-2 shadow-sm`
                          : "bg-white text-gray-600 border border-gray-200 hover:bg-gray-50"
                      }`}
                    >
                      {config.label}
                    </button>
                  ))}
                </div>
              </div>

              {/* 마감일 선택 */}
              <div>
                <label className="block text-xs font-medium text-gray-700 mb-2">
                  <Calendar className="w-3 h-3 inline mr-1" />
                  마감일
                </label>
                <div className="flex space-x-2">
                  {/* 빠른 선택 버튼들 */}
                  <button
                    onClick={() =>
                      setDueDate(new Date().toISOString().split("T")[0])
                    }
                    className="px-3 py-2 text-sm bg-white border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors"
                  >
                    오늘
                  </button>
                  <button
                    onClick={() => {
                      const tomorrow = new Date();
                      tomorrow.setDate(tomorrow.getDate() + 1);
                      setDueDate(tomorrow.toISOString().split("T")[0]);
                    }}
                    className="px-3 py-2 text-sm bg-white border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors"
                  >
                    내일
                  </button>
                  <input
                    type="date"
                    value={dueDate}
                    onChange={(e) => setDueDate(e.target.value)}
                    className="flex-1 px-3 py-2 text-sm border border-gray-200 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent outline-none"
                  />
                </div>
              </div>
            </div>
          )}
        </div>

        {/* 추가 버튼 */}
        <button
          onClick={handleAdd}
          disabled={!value.trim()}
          className={`px-4 py-2 rounded-xl transition-all self-start ${
            value.trim()
              ? "bg-green-600 hover:bg-green-700 text-white shadow-lg hover:shadow-xl transform hover:scale-105"
              : "bg-gray-200 text-gray-400 cursor-not-allowed"
          }`}
          title="할 일 추가"
        >
          <Plus className="w-5 h-5" />
        </button>
      </div>

      {/* 선택된 옵션 미리보기 */}
      {!showOptions && (value.trim() || priority !== "medium") && (
        <div className="mt-2 flex items-center space-x-2 text-xs">
          <span
            className={`px-2 py-1 rounded-full ${currentPriority.bg} ${currentPriority.color} font-medium`}
          >
            {currentPriority.label}
          </span>
          <span className="text-gray-500">
            📅 {new Date(dueDate).toLocaleDateString("ko-KR")}
          </span>
        </div>
      )}

      {/* 도움말 텍스트 */}
      <div className="mt-2 text-xs text-gray-500 flex items-center justify-between">
        <span>Enter 키를 눌러 빠르게 추가</span>
        <span>{value.length}자</span>
      </div>
    </div>
  );
};

// 데모를 위한 래퍼
const TaskInputDemo = () => {
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState("");

  const handleAdd = ({ title, priority, dueDate }) => {
    setTasks((prev) => [
      {
        id: Date.now(),
        title,
        priority,
        dueDate,
        completed: false,
      },
      ...prev,
    ]);
    setInputValue("");
  };

  const priorityColors = {
    high: "border-l-4 border-red-500 bg-red-50",
    medium: "border-l-4 border-yellow-500 bg-yellow-50",
    low: "border-l-4 border-blue-500 bg-blue-50",
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-gray-50 min-h-screen">
      <h1 className="text-2xl font-bold text-gray-800 mb-6">할 일 관리</h1>

      {/* 입력 영역 */}
      <div className="bg-white rounded-xl shadow-lg overflow-hidden mb-6">
        <TaskInput
          value={inputValue}
          onChange={setInputValue}
          onAdd={handleAdd}
        />
      </div>

      {/* 작업 목록 */}
      <div className="space-y-3">
        {tasks.map((task) => (
          <div
            key={task.id}
            className={`p-4 bg-white rounded-lg shadow ${
              priorityColors[task.priority]
            }`}
          >
            <h3 className="font-medium text-gray-800">{task.title}</h3>
            <div className="mt-2 flex items-center space-x-3 text-sm text-gray-600">
              <span className="capitalize">{task.priority}</span>
              <span>•</span>
              <span>{new Date(task.dueDate).toLocaleDateString("ko-KR")}</span>
            </div>
          </div>
        ))}
      </div>

      {tasks.length === 0 && (
        <div className="text-center py-12 text-gray-400">
          할 일을 추가해보세요!
        </div>
      )}
    </div>
  );
};

export default TaskInputDemo;
