import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // 추가
import { TABS } from "./utils/constants";
import TabNavigation from "./components/layout/TabNavigation";
import ChatView from "./components/chat/ChatView";
import NoticeView from "./components/notice/NoticeView";
import CalendarView from "./components/calendar/CalendarView";
import ScheduleView from "./components/schedule/ScheduleView";

const Dashboard = ({ user, logout }) => {
  const [activeTab, setActiveTab] = useState(TABS.CHAT);
  const navigate = useNavigate(); // useNavigate 훅 사용

  const renderContent = () => {
    switch (activeTab) {
      case TABS.CHAT:
        return <ChatView user={user} />;
      case TABS.NOTICES:
        return <NoticeView user={user} />;
      case TABS.CALENDAR:
        return <CalendarView user={user} />;
      case TABS.MY_SCHEDULE:
        return (
          <ScheduleView
            user={user}
            onLoginClick={() => navigate("/login")} // 로그인 페이지로 이동
          />
        );
      default:
        return <ChatView user={user} />;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50 p-6">
      <div className="max-w-7xl mx-auto">
        {/* 헤더 */}
        <div className="flex justify-between items-center mb-6">
          <div className="text-center flex-1">
            <h1 className="text-4xl font-bold text-gray-800 mb-2">
              A_hub 통합 관리 시스템
            </h1>
            <p className="text-gray-600">
              공지사항, 일정, 챗봇을 한 곳에서 관리하세요
            </p>
          </div>
          {user && (
            <button
              onClick={logout}
              className="ml-4 px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600"
            >
              로그아웃
            </button>
          )}
        </div>

        {/* 탭 네비게이션 */}
        <TabNavigation activeTab={activeTab} setActiveTab={setActiveTab} />

        {/* 메인 콘텐츠 영역 */}
        <div
          className="bg-white rounded-2xl shadow-xl overflow-hidden mt-4"
          style={{ height: "calc(100vh - 240px)" }}
        >
          {renderContent()}
        </div>

        {/* 푸터 */}
        <div className="mt-6 text-center text-sm text-gray-500">
          <p>© 2025 A_hub. All rights reserved.</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
