import { useState, useEffect } from "react";
import axios from "axios";

const API_BASE = "http://localhost:8080/api";

const useScheduleCalendar = (user) => {
  const [tasks, setTasks] = useState([]);
  const [events, setEvents] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentDate, setCurrentDate] = useState(new Date());

  const loadSchedules = async () => {
    if (!user) return;

    try {
      setLoading(true);
      const response = await axios.get(`${API_BASE}/schedules/user/${user.id}`);
      setTasks(response.data);

      const scheduleEvents = response.data.map((schedule) => ({
        id: `schedule-${schedule.id}`,
        date: new Date(schedule.dueDate),
        title: schedule.title,
        type: "schedule",
        priority: schedule.priority,
        completed: schedule.completed,
      }));
      setEvents(scheduleEvents);
    } catch (err) {
      console.error("Failed to load schedules:", err);
      setError("스케줄을 불러오는데 실패했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const loadEvents = async () => {
    if (!user) return;
    try {
      const response = await axios.get(`${API_BASE}/events/user/${user.id}`);
      const calendarEvents = response.data.map((event) => ({
        id: `event-${event.id}`,
        date: new Date(event.eventDate),
        title: event.title,
        type: event.eventType || "event",
        time: event.eventTime,
      }));
      setEvents((prev) => {
        const scheduleEvents = prev.filter((e) => e.type === "schedule");
        return [...scheduleEvents, ...calendarEvents];
      });
    } catch (err) {
      console.error("Failed to load events:", err);
    }
  };

  useEffect(() => {
    loadSchedules();
    loadEvents();
  }, [user]);

  // ... addTask, toggleComplete, deleteTask 기존 로직 유지
  const addTask = async () => {
    if (!newTask.trim() || !user) return;

    try {
      const response = await axios.post(
        `${API_BASE}/schedules?userId=${user.id}`,
        {
          title: newTask,
          priority: "medium",
          dueDate: new Date().toISOString().split("T")[0],
          description: "",
        }
      );

      setTasks((prev) => [response.data, ...prev]);
      setNewTask("");

      const newEvent = {
        id: `schedule-${response.data.id}`,
        date: new Date(response.data.dueDate),
        title: response.data.title,
        type: "schedule",
        priority: response.data.priority,
        completed: false,
      };
      setEvents((prev) => [...prev, newEvent]);
    } catch (err) {
      console.error(err);
      setError("작업 추가에 실패했습니다.");
    }
  };

  const toggleComplete = async (id) => {
    try {
      const response = await axios.put(`${API_BASE}/schedules/${id}/toggle`);
      setTasks((prev) =>
        prev.map((task) => (task.id === id ? response.data : task))
      );
      setEvents((prev) =>
        prev.map((event) =>
          event.id === `schedule-${id}`
            ? { ...event, completed: response.data.completed }
            : event
        )
      );
    } catch (err) {
      console.error(err);
    }
  };

  const deleteTask = async (id) => {
    try {
      await axios.delete(`${API_BASE}/schedules/${id}`);
      setTasks((prev) => prev.filter((task) => task.id !== id));
      setEvents((prev) =>
        prev.filter((event) => event.id !== `schedule-${id}`)
      );
    } catch (err) {
      console.error(err);
    }
  };

  const getDaysInMonth = () => {
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    const firstDay = new Date(year, month, 1).getDay();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    return { firstDay, daysInMonth };
  };

  const getEventsForDate = (day) => {
    const targetDate = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth(),
      day
    );
    return events.filter(
      (event) =>
        event.date.getDate() === targetDate.getDate() &&
        event.date.getMonth() === targetDate.getMonth() &&
        event.date.getFullYear() === targetDate.getFullYear()
    );
  };

  const prevMonth = () => {
    setCurrentDate(
      new Date(currentDate.getFullYear(), currentDate.getMonth() - 1)
    );
  };

  const nextMonth = () => {
    setCurrentDate(
      new Date(currentDate.getFullYear(), currentDate.getMonth() + 1)
    );
  };

  return {
    tasks,
    newTask,
    setNewTask,
    addTask,
    toggleComplete,
    deleteTask,
    currentDate,
    events,
    getDaysInMonth,
    getEventsForDate,
    prevMonth,
    nextMonth,
    loading,
    error,
    refresh: () => {
      loadSchedules();
      loadEvents();
    },
  };
};

export default useScheduleCalendar;
