import { useState, useEffect } from "react";
import axios from "axios";

const API_BASE = "http://localhost:8080/api";

const useScheduleCalendar = (user) => {
  const [tasks, setTasks] = useState([]);
  const [events, setEvents] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentDate, setCurrentDate] = useState(new Date());

  // ---------- 1️⃣ 초기 데이터 로드 ----------
  const loadSchedules = async () => {
    if (!user) return;
    try {
      setLoading(true);
      const response = await axios.get(`${API_BASE}/schedules/user/${user.id}`);
      const schedules = response.data;
      setTasks(schedules);

      const scheduleEvents = schedules.map((schedule) => ({
        id: `schedule-${schedule.id}`,
        date: new Date(schedule.dueDate),
        title: schedule.title,
        type: "schedule",
        priority: schedule.priority,
        completed: schedule.completed,
      }));

      setEvents((prev) => {
        // 기존 이벤트 중 schedule만 제거하고 새로 세팅
        const nonScheduleEvents = prev.filter((e) => e.type !== "schedule");
        return [...nonScheduleEvents, ...scheduleEvents];
      });
    } catch (err) {
      console.error("Failed to load schedules:", err);
      setError("스케줄을 불러오는데 실패했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const loadEvents = async () => {
    if (!user) return;
    try {
      const response = await axios.get(`${API_BASE}/events/user/${user.id}`);
      const calendarEvents = response.data.map((event) => ({
        id: `event-${event.id}`,
        date: new Date(event.eventDate),
        title: event.title,
        type: event.eventType || "event",
        time: event.eventTime,
      }));

      setEvents((prev) => {
        // schedule 이벤트는 유지
        const scheduleEvents = prev.filter((e) => e.type === "schedule");
        return [...scheduleEvents, ...calendarEvents];
      });
    } catch (err) {
      console.error("Failed to load events:", err);
    }
  };

  useEffect(() => {
    loadSchedules();
    loadEvents();
  }, [user]);

  // ---------- 2️⃣ 할 일 추가 ----------
  const addTask = async () => {
    if (!newTask.trim() || !user) return;

    try {
      const response = await axios.post(
        `${API_BASE}/schedules?userId=${user.id}`,
        {
          title: newTask,
          priority: "medium",
          dueDate: new Date().toISOString().split("T")[0],
          description: "",
        }
      );

      const savedTask = response.data;

      // tasks 업데이트
      setTasks((prev) => [savedTask, ...prev]);

      // events 업데이트
      const newEvent = {
        id: `schedule-${savedTask.id}`,
        date: new Date(savedTask.dueDate),
        title: savedTask.title,
        type: "schedule",
        priority: savedTask.priority,
        completed: savedTask.completed,
      };
      setEvents((prev) => {
        // 기존 schedule 이벤트 중 동일 id 제거 후 추가
        const filtered = prev.filter((e) => e.id !== newEvent.id);
        return [...filtered, newEvent];
      });

      setNewTask("");
    } catch (err) {
      console.error(err);
      setError("작업 추가에 실패했습니다.");
    }
  };

  // ---------- 3️⃣ 완료 토글 ----------
  const toggleComplete = async (id) => {
    try {
      const response = await axios.put(`${API_BASE}/schedules/${id}/toggle`);
      const updatedTask = response.data;

      setTasks((prev) =>
        prev.map((task) => (task.id === id ? updatedTask : task))
      );

      setEvents((prev) =>
        prev.map((event) =>
          event.id === `schedule-${id}`
            ? { ...event, completed: updatedTask.completed }
            : event
        )
      );
    } catch (err) {
      console.error(err);
    }
  };

  // ---------- 4️⃣ 삭제 ----------
  const deleteTask = async (id) => {
    try {
      await axios.delete(`${API_BASE}/schedules/${id}`);
      setTasks((prev) => prev.filter((task) => task.id !== id));
      setEvents((prev) =>
        prev.filter((event) => event.id !== `schedule-${id}`)
      );
    } catch (err) {
      console.error(err);
    }
  };

  // ---------- 5️⃣ 캘린더 관련 헬퍼 ----------
  const getDaysInMonth = () => {
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    const firstDay = new Date(year, month, 1).getDay();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    return { firstDay, daysInMonth };
  };

  const getEventsForDate = (day) => {
    const targetDate = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth(),
      day
    );
    return events.filter(
      (event) =>
        event.date.getDate() === targetDate.getDate() &&
        event.date.getMonth() === targetDate.getMonth() &&
        event.date.getFullYear() === targetDate.getFullYear()
    );
  };

  const prevMonth = () =>
    setCurrentDate(
      new Date(currentDate.getFullYear(), currentDate.getMonth() - 1)
    );

  const nextMonth = () =>
    setCurrentDate(
      new Date(currentDate.getFullYear(), currentDate.getMonth() + 1)
    );

  return {
    tasks,
    newTask,
    setNewTask,
    addTask,
    toggleComplete,
    deleteTask,
    currentDate,
    events,
    getDaysInMonth,
    getEventsForDate,
    prevMonth,
    nextMonth,
    loading,
    error,
    refresh: () => {
      loadSchedules();
      loadEvents();
    },
  };
};

export default useScheduleCalendar;
