import React, { useRef, useEffect } from "react";
import useChat from "../../hooks/useChat";
import useConnection from "../../hooks/useConnection";

// 컴포넌트들을 하나씩 주석 해제하면서 테스트하세요
import ChatHeader from "./ChatHeader";
import ChatMessage from "./ChatMessage";
import TypingIndicator from "./TypingIndicator";
// import ChatInput from "./ChatInput";

const ChatView = () => {
  const { messages, isTyping, sendMessage } = useChat();
  const { status, error, check } = useConnection();
  const [input, setInput] = React.useState("");
  const messagesEndRef = useRef(null);

  // 메시지 추가 시 자동 스크롤
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, isTyping]);

  // 메시지 전송 핸들러
  const handleSend = async () => {
    if (!input.trim()) return;

    const message = input;
    setInput("");
    await sendMessage(message);
  };

  // 추천 질문
  const suggestions = [
    "최신 공지 알려줘",
    "중요 공지가 뭐야?",
    "내 부서 공지는?",
  ];

  const isConnected = status === "connected";

  return (
    <div className="h-full flex flex-col">
      {/* 헤더 */}
      <ChatHeader status={status} error={error} onRefresh={check} />

      {/* 메시지 영역 */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gray-50">
        {messages.map((msg) => (
          <ChatMessage key={msg.id} msg={msg} />
        ))}

        {isTyping && <TypingIndicator />}

        <div ref={messagesEndRef} />
      </div>

      {/* 입력 영역 */}
      <div className="p-4 border-t bg-white">
        <div className="flex space-x-3">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === "Enter") {
                handleSend();
              }
            }}
            placeholder={
              isConnected
                ? "공지사항에 대해 궁금한 것을 물어보세요..."
                : "서버 연결을 기다리는 중..."
            }
            disabled={!isConnected}
            className="flex-1 px-4 py-3 border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 outline-none"
          />
          <button
            onClick={handleSend}
            disabled={!input.trim() || !isConnected}
            className={`px-6 py-3 rounded-xl ${
              input.trim() && isConnected
                ? "bg-blue-600 hover:bg-blue-700 text-white"
                : "bg-gray-200 text-gray-400 cursor-not-allowed"
            }`}
          >
            전송
          </button>
        </div>

        {/* 추천 질문 */}
        <div className="mt-3 flex flex-wrap gap-2">
          {suggestions.map((suggestion, idx) => (
            <button
              key={idx}
              onClick={() => setInput(suggestion)}
              disabled={!isConnected}
              className="px-3 py-1 text-xs bg-blue-50 text-blue-600 rounded-full hover:bg-blue-100 border border-blue-200"
            >
              {suggestion}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ChatView;
