import React from "react";
import { Clock } from "lucide-react";
import ScheduleHeader from "./ScheduleHeader";
import TaskItem from "./TaskItem";
import TaskInput from "./TaskInput";
import useScheduleCalendar from "../../hooks/useScheduleCalendar";

const ScheduleView = ({ user }) => {
  // user가 없으면 훅 호출하지 않음
  const {
    tasks = [],
    newTask = "",
    setNewTask = () => {},
    addTask = () => {},
    toggleComplete = () => {},
    deleteTask = () => {},
    loading = false,
    error = null,
  } = user ? useScheduleCalendar(user) : {};

  const completedTasks = tasks.filter((t) => t.completed).length;

  if (!user) {
    return (
      <div className="h-full flex items-center justify-center text-gray-500">
        로그인 후 사용 가능합니다.
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* 헤더 */}
      <ScheduleHeader
        totalTasks={tasks.length}
        completedTasks={completedTasks}
      />

      {/* 에러 표시 */}
      {error && (
        <div className="p-2 bg-red-100 text-red-700 text-sm">{error}</div>
      )}

      {/* 할 일 목록 */}
      <div className="flex-1 overflow-y-auto p-6 space-y-3 bg-gray-50">
        {loading ? (
          <div className="text-center py-12 text-gray-500">로딩중...</div>
        ) : tasks.length === 0 ? (
          <div className="text-center py-12 text-gray-500">
            <Clock className="w-12 h-12 text-gray-300 mx-auto mb-3" />
            <p className="text-lg font-medium mb-1">할 일이 없습니다</p>
            <p className="text-sm">아래에서 새로운 할 일을 추가해보세요</p>
          </div>
        ) : (
          <>
            {/* 진행 중 */}
            {tasks.filter((t) => !t.completed).length > 0 && (
              <div>
                <h3 className="text-sm font-semibold text-gray-600 mb-3 px-2">
                  진행 중 ({tasks.filter((t) => !t.completed).length})
                </h3>
                <div className="space-y-3">
                  {tasks
                    .filter((t) => !t.completed)
                    .map((task) => (
                      <TaskItem
                        key={task.id}
                        task={task}
                        onToggle={toggleComplete}
                        onDelete={deleteTask}
                      />
                    ))}
                </div>
              </div>
            )}

            {/* 완료된 작업 */}
            {tasks.filter((t) => t.completed).length > 0 && (
              <div className="mt-6">
                <h3 className="text-sm font-semibold text-gray-600 mb-3 px-2">
                  완료됨 ({tasks.filter((t) => t.completed).length})
                </h3>
                <div className="space-y-3">
                  {tasks
                    .filter((t) => t.completed)
                    .map((task) => (
                      <TaskItem
                        key={task.id}
                        task={task}
                        onToggle={toggleComplete}
                        onDelete={deleteTask}
                      />
                    ))}
                </div>
              </div>
            )}
          </>
        )}
      </div>

      {/* 할 일 추가 입력 */}
      <TaskInput value={newTask} onChange={setNewTask} onAdd={addTask} />
    </div>
  );
};

export default ScheduleView;
