import React, { useState, useEffect, useRef, useCallback } from "react";
import {
  Send,
  Bot,
  User,
  Sparkles,
  AlertCircle,
  Wifi,
  WifiOff,
  RefreshCw,
} from "lucide-react";

const API_URL = "http://localhost:8080/api/chat";

const NoticeChat = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      type: "bot",
      text: "ÏïàÎÖïÌïòÏÑ∏Ïöî! A_hub Í≥µÏßÄ Ï±óÎ¥áÏûÖÎãàÎã§. Í∂ÅÍ∏àÌïú Í≥µÏßÄÏÇ¨Ìï≠ÏùÑ Î¨ºÏñ¥Î≥¥ÏÑ∏Ïöî! ‚ú®",
      timestamp: new Date(),
    },
  ]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState("disconnected");
  const [error, setError] = useState(null);

  const messagesEndRef = useRef(null);
  const messageIdRef = useRef(2);

  // ÏûêÎèô Ïä§ÌÅ¨Î°§
  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  // Î©îÏãúÏßÄ Ï∂îÍ∞Ä Ìï®Ïàò
  const addMessage = useCallback((type, text, error = false) => {
    const newMessage = {
      id: messageIdRef.current++,
      type,
      text,
      timestamp: new Date(),
      error,
    };
    setMessages((prev) => [...prev, newMessage]);
  }, []);

  // ÏÑúÎ≤Ñ ÏÉÅÌÉú Ï≤¥ÌÅ¨
  const checkServerStatus = useCallback(async () => {
    try {
      const response = await fetch(`${API_URL}/query`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify("ÌÖåÏä§Ìä∏"),
      });

      if (response.ok) {
        setConnectionStatus("connected");
        setError(null);
        addMessage("bot", "ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞ÎêòÏóàÏäµÎãàÎã§!");
        return true;
      } else {
        throw new Error(`Server returned ${response.status}`);
      }
    } catch (error) {
      console.error("Server check failed:", error);
      setConnectionStatus("disconnected");
      setError("ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞Ìï† Ïàò ÏóÜÏäµÎãàÎã§. ÏÑúÎ≤ÑÍ∞Ä Ïã§Ìñâ Ï§ëÏù∏ÏßÄ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.");
      return false;
    }
  }, [addMessage]);

  // Í≥µÏßÄÏÇ¨Ìï≠ Ìè¨Îß∑ÌåÖ
  const formatNotices = (notices) => {
    if (!notices || notices.length === 0) {
      return "Ï£ÑÏÜ°Ìï©ÎãàÎã§. Í¥ÄÎ†®Îêú Í≥µÏßÄÏÇ¨Ìï≠ÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§. Îã§Î•∏ ÌÇ§ÏõåÎìúÎ°ú Í≤ÄÏÉâÌï¥Î≥¥ÏãúÍ≤†Ïñ¥Ïöî?";
    }

    let response = "üîç Í≤ÄÏÉâ Í≤∞Í≥ºÏûÖÎãàÎã§:\n\n";

    notices.slice(0, 3).forEach((notice, index) => {
      // Ï†úÎ™©Ïù¥ ÏóÜÏúºÎØÄÎ°ú ÏàúÎ≤àÏù¥ÎÇò IDÎ°ú ÌëúÏãú
      const title = `Í≥µÏßÄÏÇ¨Ìï≠ #${notice.seq || notice.id}`;
      response += `${index + 1}. **${title}**\n`;

      // ÎÇ¥Ïö© ÌëúÏãú
      let content = notice.content || "";
      if (content.length > 200) {
        content = content.substring(0, 200) + "...";
      }
      if (content) {
        response += content + "\n";
      }

      // Í≤åÏãúÏùº ÌëúÏãú (postDate Ïö∞ÏÑ†, ÏóÜÏúºÎ©¥ registerDate)
      if (notice.postDate) {
        response += `üìÖ Í≤åÏãúÏùº: ${notice.postDate}\n`;
      } else if (notice.registerDate) {
        response += `üìÖ Îì±Î°ùÏùº: ${notice.registerDate}\n`;
      }

      // Î∂ÄÏÑú Ï†ïÎ≥¥
      if (notice.department && notice.department.trim()) {
        response += `üè¢ Î∂ÄÏÑú: ${notice.department}\n`;
      }

      // Ïó∞ÎùΩÏ≤ò Ï†ïÎ≥¥
      if (notice.contact && notice.contact.trim()) {
        response += `üìû Ïó∞ÎùΩÏ≤ò: ${notice.contact}\n`;
      }

      // URL Ï†ïÎ≥¥
      if (notice.url && notice.url.trim()) {
        response += `üîó ÎßÅÌÅ¨: ${notice.url}\n`;
      }

      response += "\n";
    });

    if (notices.length > 3) {
      response += `üìù Ï¥ù ${notices.length}Í±¥Ïùò Í¥ÄÎ†® Í≥µÏßÄÍ∞Ä Îçî ÏûàÏäµÎãàÎã§.\n`;
    }

    response += "Îçî ÏûêÏÑ∏Ìïú Ï†ïÎ≥¥Í∞Ä ÌïÑÏöîÌïòÏãúÎ©¥ Íµ¨Ï≤¥Ï†ÅÏúºÎ°ú ÏßàÎ¨∏Ìï¥ Ï£ºÏÑ∏Ïöî!";

    return response;
  };

  // Î©îÏãúÏßÄ Ï†ÑÏÜ°
  const sendMessage = useCallback(async () => {
    if (!input.trim()) return;

    try {
      // ÏÇ¨Ïö©Ïûê Î©îÏãúÏßÄ Ï∂îÍ∞Ä
      addMessage("user", input);
      const userInput = input;
      setInput("");
      setIsTyping(true);

      // API Ìò∏Ï∂ú
      const response = await fetch(`${API_URL}/query`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(userInput),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const notices = await response.json();
      const botResponse = formatNotices(notices);

      setIsTyping(false);
      addMessage("bot", botResponse);
    } catch (error) {
      console.error("Failed to send message:", error);
      setIsTyping(false);
      addMessage(
        "bot",
        "Î©îÏãúÏßÄ Ï†ÑÏÜ° Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. ÏÑúÎ≤Ñ Ïó∞Í≤∞ÏùÑ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.",
        true
      );
      setConnectionStatus("disconnected");
      setError("ÏÑúÎ≤Ñ ÌÜµÏã† Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  }, [input, addMessage, formatNotices]);

  // Ï∂îÏ≤ú ÏßàÎ¨∏ ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨
  const handleSuggestionClick = useCallback((suggestion) => {
    setInput(suggestion);
  }, []);

  // ÌÇ§Î≥¥Îìú Ïù¥Î≤§Ìä∏
  const handleKeyDown = useCallback(
    (e) => {
      if (e.key === "Enter" && !e.shiftKey) {
        e.preventDefault();
        sendMessage();
      }
    },
    [sendMessage]
  );

  // Ïó∞Í≤∞ ÏÉÅÌÉúÏóê Îî∞Î•∏ Ïä§ÌÉÄÏùº
  const getConnectionStatusColor = () => {
    switch (connectionStatus) {
      case "connected":
        return "bg-green-400";
      case "connecting":
        return "bg-yellow-400 animate-pulse";
      case "disconnected":
        return "bg-red-400";
      default:
        return "bg-gray-400";
    }
  };

  const getConnectionIcon = () => {
    return connectionStatus === "connected" ? (
      <Wifi className="w-4 h-4 text-white" />
    ) : (
      <WifiOff className="w-4 h-4 text-white" />
    );
  };

  // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú ÏÑúÎ≤Ñ ÏÉÅÌÉú ÌôïÏù∏
  useEffect(() => {
    setConnectionStatus("connecting");
    checkServerStatus();
  }, [checkServerStatus]);

  // Î©îÏãúÏßÄ Ï∂îÍ∞Ä Ïãú Ïä§ÌÅ¨Î°§
  useEffect(() => {
    scrollToBottom();
  }, [messages, scrollToBottom]);

  const isConnected = connectionStatus === "connected";

  return (
    <div className="bg-white rounded-2xl shadow-xl border border-gray-100 overflow-hidden">
      {/* Ìó§Îçî */}
      <div className="bg-gradient-to-r from-blue-600 via-purple-600 to-blue-700 px-6 py-4">
        <div className="flex items-center space-x-3">
          <div className="relative">
            <div className="w-10 h-10 bg-white/20 rounded-full flex items-center justify-center backdrop-blur-sm">
              <Bot className="w-6 h-6 text-white" />
            </div>
            <div
              className={`absolute -top-1 -right-1 w-4 h-4 ${getConnectionStatusColor()} rounded-full border-2 border-white flex items-center justify-center`}
            >
              {connectionStatus === "connecting" ? null : getConnectionIcon()}
            </div>
          </div>
          <div className="flex-1">
            <h3 className="text-white font-semibold text-lg">
              A_hub Í≥µÏßÄ Ïñ¥ÏãúÏä§ÌÑ¥Ìä∏
            </h3>
            <p className="text-blue-100 text-sm">
              {connectionStatus === "connected" &&
                "Ìï≠ÏÉÅ ÎèÑÏõÄÏùÑ ÎìúÎ¶¥ Ï§ÄÎπÑÍ∞Ä ÎêòÏñ¥ÏûàÏñ¥Ïöî"}
              {connectionStatus === "connecting" && "ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞ÌïòÎäî Ï§ë..."}
              {connectionStatus === "disconnected" && "Ïó∞Í≤∞Ïù¥ ÎÅäÏñ¥Ï°åÏäµÎãàÎã§"}
            </p>
          </div>
          <div className="flex items-center space-x-2">
            {error && <AlertCircle className="w-5 h-5 text-red-300" />}
            <Sparkles className="w-5 h-5 text-yellow-300 animate-pulse" />
            <button
              onClick={checkServerStatus}
              className="p-1 hover:bg-white/10 rounded"
              title="ÏÑúÎ≤Ñ ÏÉÅÌÉú ÌôïÏù∏"
            >
              <RefreshCw className="w-4 h-4 text-white" />
            </button>
          </div>
        </div>
      </div>

      {/* ÏóêÎü¨ Î©îÏãúÏßÄ */}
      {error && (
        <div className="bg-red-50 border-b border-red-200 px-4 py-2">
          <div className="flex items-center space-x-2 text-red-700 text-sm">
            <AlertCircle className="w-4 h-4" />
            <span>{error}</span>
            <button
              onClick={checkServerStatus}
              className="ml-auto px-2 py-1 text-xs bg-red-100 hover:bg-red-200 rounded transition-colors"
            >
              Îã§Ïãú Ïó∞Í≤∞
            </button>
          </div>
        </div>
      )}

      {/* Ïó∞Í≤∞ ÏïàÎÇ¥ */}
      {!isConnected && !error && (
        <div className="bg-blue-50 border-b border-blue-200 px-4 py-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2 text-blue-700 text-sm">
              <div className="w-4 h-4 border-2 border-blue-400 border-t-transparent rounded-full animate-spin"></div>
              <span>REST API ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞ÌïòÎäî Ï§ë...</span>
            </div>
            <div className="text-xs text-blue-600">{API_URL}</div>
          </div>
        </div>
      )}

      {/* Î©îÏãúÏßÄ ÏòÅÏó≠ */}
      <div className="h-96 overflow-y-auto p-4 space-y-4 bg-gray-50">
        {messages.map((msg) => (
          <div
            key={msg.id}
            className={`flex ${
              msg.type === "user" ? "justify-end" : "justify-start"
            } animate-fade-in`}
          >
            <div className="flex items-start space-x-3 max-w-xs lg:max-w-md">
              {msg.type === "bot" && (
                <div
                  className={`w-8 h-8 ${
                    msg.error
                      ? "bg-red-500"
                      : "bg-gradient-to-r from-blue-500 to-purple-500"
                  } rounded-full flex items-center justify-center flex-shrink-0`}
                >
                  {msg.error ? (
                    <AlertCircle className="w-5 h-5 text-white" />
                  ) : (
                    <Bot className="w-5 h-5 text-white" />
                  )}
                </div>
              )}

              <div
                className={`px-4 py-3 rounded-2xl shadow-sm ${
                  msg.type === "user"
                    ? "bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-br-md"
                    : msg.error
                    ? "bg-red-50 text-red-800 rounded-bl-md border border-red-200"
                    : "bg-white text-gray-800 rounded-bl-md border border-gray-100"
                }`}
              >
                <p className="text-sm leading-relaxed whitespace-pre-line">
                  {msg.text}
                </p>
                <p className="text-xs opacity-70 mt-1">
                  {msg.timestamp.toLocaleTimeString()}
                </p>
              </div>

              {msg.type === "user" && (
                <div className="w-8 h-8 bg-gradient-to-r from-gray-400 to-gray-500 rounded-full flex items-center justify-center flex-shrink-0">
                  <User className="w-5 h-5 text-white" />
                </div>
              )}
            </div>
          </div>
        ))}

        {isTyping && (
          <div className="flex justify-start animate-fade-in">
            <div className="flex items-start space-x-3">
              <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full flex items-center justify-center">
                <Bot className="w-5 h-5 text-white" />
              </div>
              <div className="bg-white px-4 py-3 rounded-2xl rounded-bl-md border border-gray-100 shadow-sm">
                <div className="flex space-x-1">
                  <div className="w-2 h-2 bg-blue-400 rounded-full animate-bounce"></div>
                  <div
                    className="w-2 h-2 bg-blue-400 rounded-full animate-bounce"
                    style={{ animationDelay: "0.1s" }}
                  ></div>
                  <div
                    className="w-2 h-2 bg-blue-400 rounded-full animate-bounce"
                    style={{ animationDelay: "0.2s" }}
                  ></div>
                </div>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* ÏûÖÎ†• ÏòÅÏó≠ */}
      <div className="p-4 border-t border-gray-100 bg-white">
        <div className="flex space-x-3 items-end">
          <div className="flex-1 relative">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder={
                isConnected
                  ? "Í≥µÏßÄÏÇ¨Ìï≠Ïóê ÎåÄÌï¥ Í∂ÅÍ∏àÌïú Í≤ÉÏùÑ Î¨ºÏñ¥Î≥¥ÏÑ∏Ïöî..."
                  : "ÏÑúÎ≤Ñ Ïó∞Í≤∞ÏùÑ Í∏∞Îã§Î¶¨Îäî Ï§ë..."
              }
              disabled={!isConnected}
              className="w-full px-4 py-3 border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none resize-none bg-gray-50 text-gray-800 placeholder-gray-500 disabled:opacity-50 disabled:cursor-not-allowed"
            />
          </div>
          <button
            onClick={sendMessage}
            disabled={!input.trim() || !isConnected}
            className={`px-4 py-3 rounded-xl transition-all duration-200 ${
              input.trim() && isConnected
                ? "bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white shadow-lg hover:shadow-xl transform hover:scale-105"
                : "bg-gray-200 text-gray-400 cursor-not-allowed"
            }`}
          >
            <Send className="w-5 h-5" />
          </button>
        </div>

        {/* Ï∂îÏ≤ú ÏßàÎ¨∏ */}
        <div className="mt-3 flex flex-wrap gap-2">
          {["ÏµúÏã† Í≥µÏßÄ ÏïåÎ†§Ï§ò", "Ï§ëÏöî Í≥µÏßÄÍ∞Ä Î≠êÏïº?", "ÎÇ¥ Î∂ÄÏÑú Í≥µÏßÄÎäî?"].map(
            (suggestion, idx) => (
              <button
                key={idx}
                onClick={() => handleSuggestionClick(suggestion)}
                disabled={!isConnected}
                className="px-3 py-1 text-xs bg-blue-50 text-blue-600 rounded-full hover:bg-blue-100 transition-colors border border-blue-200 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {suggestion}
              </button>
            )
          )}
        </div>

        {/* Ïó∞Í≤∞ ÏÉÅÌÉú ÌëúÏãú */}
        <div className="mt-2 text-xs text-gray-500 flex items-center justify-between">
          <span>
            ÏÉÅÌÉú:{" "}
            {connectionStatus === "connected"
              ? "Ïó∞Í≤∞Îê®"
              : connectionStatus === "connecting"
              ? "Ïó∞Í≤∞ Ï§ë..."
              : "Ïó∞Í≤∞ ÎÅäÍπÄ"}
          </span>
          <span>REST API Ïó∞Í≤∞</span>
        </div>
      </div>
    </div>
  );
};

export default NoticeChat;
